rule.if:
if (ZONE == null)
	return null;
var lv = try(DS.ZONE_LEVEL[0][0],3);
if (lv == 1) {
	if (AMOUNT > 1500000) {
		return '一类城市最高150万有效保额';
	}
} else if (lv == 2) {
	if (AMOUNT > 1200000) {
		return '二类城市最高120万有效保额';
	}
} else {
	if (AMOUNT > 1000000) {
		return '普通地区最高100万有效保额';
	}
}
return null;
