rule.if:
if (AGE >= 18 and AGE le 45 and AMOUNT lt 500000)
	return "18~45周岁，保额最低50万";
if (AGE >= 46 and AGE le 50 and AMOUNT lt 300000)
	return "46~50周岁，保额最低30万";
if (ZONE == null)
	return null;
var city = 3;
var code = str(ZONE, 0, 4);
var codeP = str(ZONE, 0, 2);
if (find(A_CITY, code) != null or find(A_CITY, codeP) != null) {
	city = 1;
} else if (find(B_CITY, code) != null) {
	city = 2;
} else if (find(C_CITY, code) != null) {
	city = 3;
} else {
	return "该产品仅允许北京、上海、广州、深圳、武汉、成都、南京、郑州、苏州9个城市投保";
}
if (city == 1) {
	if (AGE >= 18 and AGE le 45 and AMOUNT lt 1000000) {
		return '北京、上海、广州、深圳地区，18~45岁最低保额为100万';
	} else if (AGE >= 46 and AGE le 50 and AMOUNT lt 600000) {
		return '北京、上海、广州、深圳地区，46~50岁最低保额为60万';
	}
/* } else if (city == 2) {
	if (AGE >= 18 and AGE le 45 and AMOUNT lt 900000) {
		return '省会地区，18~45岁最低保额为90万';
	} else if (AGE >= 46 and AGE le 50 and AMOUNT lt 500000) {
		return '省会地区，46~50岁最低保额为50万';
	} */
} else {
	if (AGE >= 18 and AGE le 45 and AMOUNT lt 600000) {
		return '非北京、上海、广州、深圳地区，18~45岁最低保额为60万';
	} else if (AGE >= 46 and AGE le 50 and AMOUNT lt 300000) {
		return '非北京、上海、广州、深圳地区，46~50岁最低保额为30万';
	}
}
return null;
