init.item:
var total = 0;
var list = PLAN.PRODUCTS;
for (var x = 0; x lt size(list); x++) {
	var p = list[x];
	if ((p.PARENT == THIS.PARENT or p == THIS.PARENT) and p != THIS and p.PAY_PERIOD == THIS.PARENT.PAY_PERIOD) {
		total += p.PREMIUM;
	}
}
return total;

var total = 0;
var list = PLAN.PRODUCTS;
for (var x = 0; x lt size(list); x++) {
	var p = list[x];
	if (p.PRODUCT_ID == 'GWL000002') {
		total = p.PREMIUM;
	}
}
return total;

