init.item:
var f_occ = [1.00, 1.25, 1.50, 2.50, 3.50, 4.50];
var f_age = AGE >= 16 ? 1 : (AGE >= 3 ? 1.25 : 1.50);
var f_sci = SOCIAL_INS == 1 ? 0.9 : 1;
var p;

if (5000 > AMOUNT)
	p = 12 * QUANTITY;
else if (10000 > AMOUNT)
	p = 60 + 8 * (QUANTITY - 5);
else
	p = 100 + 4 * (QUANTITY - 10);

return p * f_occ[OCCUPATION_CATEGORY-1] * f_age * f_sci;
