	if (AGE >= 18 and AGE le 40 and AMOUNT > 300000 and AMOUNT le 500000) {
		return '除北京、上海、深圳、广州、成都、杭州、武汉、重庆、天津、南京、苏州、长沙、青岛、佛山、西安、济南、东莞、厦门、福州、无锡、合肥、郑州、宁波、大连、石家庄、南宁、南昌、昆明、烟台、泉州、珠海、惠州、温州、太原外，18~40岁最高保额为30万';
	}
}
return null;
