init.item:
var btwn = function(mi, ma, v){
	if(v lt mi or v > ma)
		return false;

	return true;
};
var a = -1;
if(btwn(0, 4, AGE)) {
	a = 0;
} else if(btwn(5, 10, AGE)) {
	a = 1;
} else {
	a = ceil(AGE / 5) - 1;
}
if(!btwn(0, 15, a)) {
	return null;
}

// 质重加油包
var addPrem = 0;
if(PROTON_HEAVY_ION){
	if(btwn(14, 35, AGE)) {
		addPrem = 10;
	} else if(btwn(36, 40, AGE)) {
		addPrem = 20;
	} else if(btwn(41, 60, AGE)) {
		addPrem = 30;
	} else if(btwn(61, 80, AGE)) {
		addPrem = 50;
	}
}

var rs = (SHARE_DEDUCTIBLE ? RS2 : RS1);
return rs[a][SOCIAL_INS ? 0 : 1] + addPrem;
